import { useState } from "react";
const App = () => {
  const [counter, setCounter] = useState(0);

  const handleClick = () => {
    console.log("we clicked");
    setCounter(counter + 1);
  };
  return (
    <div>
      {counter}
      <button onClick={handleClick}>Plus</button>
      /
      <button onClick={() => setCounter(counter - 1)}>Minus</button>
    </div>
  );
};

export default App;
